import React, { useEffect, useState } from "react";
import Founders from "../assets/founders.png";
import Catalogue from "../assets/catalogue.png";
import { Link } from "react-router-dom";
 
export default function Landing() {
 
 // This following section will display the table with the records of individuals.
 return (
   <div>

    <div className="d-flex flex-column center bg-image">
      <img src={Catalogue} alt="Founders"></img>
      <div class="mask text-light d-flex justify-content-center flex-column text-center">

      </div>
      <h3 className="mask align-self-center">Choose the skills that YOU want to learn!</h3>
      <button>
        Browse Classes
      </button>
    </div>
     
    <div className="d-flex flex-row center">
      <img src={Founders} alt="Founders"></img>
      <div>
        <h4>At Teecha</h4>
        <p>Education is at the heart of what we stand for—that’s why we only partner with teachers that pledge to use environmentally-friendly teaching methods. 

        </p>
        <button>
          Read our story
        </button>
      </div>
      
    </div>

   </div>
 );
}

